script:
frappe.ui.form.on('Mode of Payment', {
	refresh(frm) {
	    // Set up field filter for custom_zra_payment_type
        frm.set_query("custom_zra_payment_type", function() {
            return {
                filters: {
                    "cd_cls": ["in", ["Payment Type", "07"]]
                }
            };
        });

	}
});

frappe.ui.form.on('Branch', {
	refresh(frm) {
        frm.add_custom_button(__("Sync from Smart Invoice"), function() {
            frappe.call({
                method: "smart_invoice_app.app.sync_branches",
        		freeze: true,
        		freeze_message: __("Updating all branches from Smart Invoice ..."),
                callback: (r)=>{
                    frm.reload_doc();
                }
            });
        });

        // Custom filter function to exclude existing users
        frm.set_query("user_id", "custom_branch_users", function(doc) {
            existing_users = frm.doc.custom_branch_users.map(row => row.user_id);
            return{
                filters: {
                    "name": ["not in", existing_users]
                }
            };
        });

	},
	after_save(frm){
	    frappe.call({
	        method: "smart_invoice_app.app.update_api_users",
	        args: {
	            branch: frm.doc.branch
	        },
    		freeze: true,
    		freeze_message: __("Updating the branch and its users ..."),
	        callback: (r)=>{
                if(r.message){
                    console.log("r", r.message || r.message === "");
    	            frm.set_value("custom_previous_branch_users", r.message);
    	            frm.refresh_fields("custom_previous_branch_users");
    	            frm.save();
    	            frm.refresh();
                }
                else if(!r.message && frm.doc.custom_branch_users.length === 0){
    	            frm.set_value("custom_previous_branch_users", "");
                }
                else{
                    console.log('none');
                }
	        }
	    });

	}
});

frappe.ui.form.on('Item Group', {
	refresh(frm) {
        // Set up field filter for custom_branch_status
        frm.set_query("custom_item_ty", function() {
            return {
                filters: {
                    "cd_cls": ["in", ["Item Type", "24"]]
                }
            };
        });
	}
})

frappe.ui.form.on('Item', {
	refresh(frm) {

        frm.add_custom_button(__("Sync with Smart Invoice"), function() {
            call_api(frm);
        });

        create_codes_if_needed(frm);

        // Set up field filters
        frm.set_query("custom_pkg_unit", function() {
            return {
                filters: {
                    "custom_code_class_name": ["in", ["17"]]
                }
            };
        });

        frm.set_query("tax_category", "taxes", function() {
            return {
                filters: {
                    "custom_cd": ["not in", ["TOT"]]
                }
            };
        });

        frm.set_query("stock_uom", function() {
            return {
                filters: {
                    "custom_code_class_name": ["in", ["10"]]
                }
            };
        });
        frm.set_query("custom_excise_tax_category", function() {
            return {
                filters: {
                    "cd_cls": ["in", ["60"]]
                }
            };
        });
        frm.set_query("custom_insurance_premium_levy_category", function() {
            return {
                filters: {
                    "cd_cls": ["in", ["61"]]
                }
            };
        });
        frm.set_query("custom_tourism_levy_type", function() {
            return {
                filters: {
                    "cd_cls": ["in", ["62"]]
                }
            };
        });
	}
});
function create_codes_if_needed(frm){
    frappe.call({
        method: "smart_invoice_app.app.create_codes_if_needed",
        args: {
            item: frm.doc.name
        },
		freeze: true,
		freeze_message: __("Updating Smart Invoice item codes ..."),
        callback: (r)=>{
        }
    });

}


function call_api(frm){
    frappe.call({
        method: "smart_invoice_app.app.sync_items",
        args: {
            initialize: true
        },
		freeze: true,
		freeze_message: __("Running Smart Invoice item sync ..."),
        callback: (r)=>{
            frm.reload_doc();
        }
    });
}

frappe.ui.form.on('Customer', {
	refresh(frm) {
        // frm.add_custom_button(__("Sync with Smart Invoice"), function() {
        //     call_api(frm);
        // });

        // Custom filter function to exclude already selected entries
        frm.set_query("branch", "custom_customer_branches", function(doc) {
            selected = frm.doc.custom_customer_branches.map(row => row.branch);
            return{
                filters: {
                    "name": ["not in", selected]
                }
            };
        });

	},
	after_save(frm){
	    call_api(frm);
	}
});

function call_api(frm){
    frappe.call({
        method: "smart_invoice_app.app.sync_customer_api",
        args: {
            customer: frm.doc
        },
		freeze: true,
		freeze_message: __("Updating Smart Invoice customer data ..."),
        callback: (r)=>{
            frm.reload_doc();
        }
    });
}

frappe.ui.form.on('UOM', {
	refresh(frm) {

        // Set up field filter for custom_cd
        frm.set_query("custom_cd", function() {
            return {
                filters: {
                    "cd_cls": ["in", ["17", "10"]]
                }
            };
        });
	}
});

frappe.ui.form.on('Company', {
	refresh(frm) {

        // frm.add_custom_button(__("Sync with Smart Invoice"), function() {
        //     call_api(frm);
        // });

        // Set up field filters
        frm.set_query("custom_default_unit_of_measure", function() {
            return {
                filters: [
                    ["custom_code_class_name", "=", "10"],
                    ["custom_code_class_name", "!=", undefined]
                ]
            };
        });

        frm.set_query("custom_packaging_unit", function() {
            return {
                filters: [
                    ["custom_code_class_name", "=", "17"],
                    ["custom_code_class_name", "!=", null]
                ]
            };
        });
	},
	custom_update_codes(frm){
	    create_codes_if_needed(frm);
	}

});


function create_codes_if_needed(frm){
    frappe.call({
        method: "smart_invoice_app.app.create_codes_if_needed",
        args: {
            item: frm.doc.name
        },
		freeze: true,
		freeze_message: __("Updating Smart Invoice item codes ..."),
        callback: (r)=>{
        }
    });

}

function call_api(frm){
    frappe.call({
        method: "smart_invoice_app.app.save_item_api",
        args: {
            item: frm.doc.name
        },
		freeze: true,
		freeze_message: __("Updating Smart Invoice item data ..."),
        callback: (r)=>{
            frm.reload_doc();
        }
    });
}

